/*
 * Gets the FCE_CONFIG APP version.
 */
DAVE_APP_VERSION_t FCE_CONFIG_GetAppVersion(void)
{
  DAVE_APP_VERSION_t version;
  version.major = (uint8_t)FCE_CONFIG_MAJOR_VERSION;
  version.minor = (uint8_t)FCE_CONFIG_MINOR_VERSION;
  version.patch = (uint8_t)FCE_CONFIG_PATCH_VERSION;

  return (version);
}

/*
 * This API initializes the FCE peripheral using the configuration input from the FCE_CONFIG APP UI.
 */
FCE_CONFIG_STATUS_t FCE_CONFIG_Init(const FCE_CONFIG_t* handle)
{
	FCE_CONFIG_STATUS_t status;

	/*Enable the FCE peripheral*/
	XMC_FCE_Enable();

	/*Initialize FCE with configuration input from the user.*/
	status = (FCE_CONFIG_STATUS_t)XMC_FCE_Init((XMC_FCE_t*)handle);

	if(status == FCE_CONFIG_STATUS_SUCCESS)
	{
		/*By default initialize the seed value with 0.*/
		XMC_FCE_InitializeSeedValue((XMC_FCE_t*)handle, 0);

		/*Enable the events*/
		XMC_FCE_EnableEvent((XMC_FCE_t*)handle, handle->event_mask);
	}

	return (status);
}


